import type { MessageTypes, SignTypedDataVersion, TypedDataV1, TypedMessage } from '@metamask/eth-sig-util';
import { Buffer } from 'buffer';
import { Auth } from '../auth';
import { rpcUrl } from '../constant';
import { AuthError, EIP1559TxData, PrefixedHexString, TxData } from '../types';
import { addHexPrefix } from '../utils/hex-utils';

class EvmService {
    constructor(private auth: Auth) {}

    private assertEVM() {
        if (this.auth.getChain()?.name?.toLowerCase() === 'solana') {
            throw AuthError.unsupportedMethod();
        }
    }

    public async sendTransaction(transaction: TxData | EIP1559TxData): Promise<PrefixedHexString> {
        this.assertEVM();
        return this.auth.sendTransaction(addHexPrefix(Buffer.from(JSON.stringify(transaction)).toString('hex')));
    }

    public async personalSign(message: PrefixedHexString): Promise<PrefixedHexString> {
        this.assertEVM();
        return this.auth.sign('personal_sign', message);
    }

    public async personalSignUniq(message: PrefixedHexString): Promise<PrefixedHexString> {
        this.assertEVM();
        return this.auth.sign('personal_sign_uniq', message);
    }

    public async signTypedData<V extends SignTypedDataVersion, T extends MessageTypes>({
        data,
        version,
    }: {
        data: V extends 'V1' ? TypedDataV1 : TypedMessage<T>;
        version: V;
    }): Promise<PrefixedHexString> {
        this.assertEVM();
        return this.auth.sign(
            `eth_signTypedData_${version.toLowerCase()}`,
            addHexPrefix(Buffer.from(JSON.stringify(data)).toString('hex'))
        );
    }

    public async signTypedDataUniq<T extends MessageTypes>(data: TypedMessage<T>): Promise<PrefixedHexString> {
        this.assertEVM();
        return this.auth.sign(
            'eth_signTypedData_v4_uniq',
            addHexPrefix(Buffer.from(JSON.stringify(data)).toString('hex'))
        );
    }

    public async getAddress(): Promise<PrefixedHexString | undefined> {
        return this.auth.getEVMAddress();
    }

    public getRpcUrl(): string {
        this.assertEVM();
        const domain = `${rpcUrl()}/evm-chain`;
        return `${domain}?chainId=${this.auth.getChain().id}&projectUuid=${this.auth.config.projectId}&projectKey=${
            this.auth.config.clientKey
        }`;
    }
}

export default EvmService;
import { Buffer } from 'buffer';

export function bufferToHex(buf: Buffer): string {
    return `0x${buf.toString('hex')}`;
}

export function intToHex(value: number): string {
    return `0x${Number(value).toString(16)}`;
}

export function isHexPrefixed(str: string): boolean {
    if (typeof str !== 'string') {
        throw new Error(`[isHexPrefixed] input must be type 'string', received type ${typeof str}`);
    }

    return str[0] === '0' && str[1] === 'x';
}

export function addHexPrefix(str: string): string {
    if (typeof str !== 'string') {
        return str;
    }

    return isHexPrefixed(str) ? str : '0x' + str;
}

export function stripHexPrefix(str: string): string {
    if (typeof str !== 'string')
        throw new Error(`[stripHexPrefix] input must be type 'string', received ${typeof str}`);

    return isHexPrefixed(str) ? str.slice(2) : str;
}

/**
 * Is the string a hex string.
 *
 * @param  value
 * @param  length
 * @returns  output the string is a hex string
 */
export function isHexString(value: string, length?: number): boolean {
    if (typeof value !== 'string' || !value.match(/^0x[0-9A-Fa-f]*$/)) return false;

    if (typeof length !== 'undefined' && length > 0 && value.length !== 2 + 2 * length) return false;

    return true;
}
import { Buffer } from 'buffer';
import { Auth } from '../auth';
import { rpcUrl } from '../constant';
import { AuthError, Base58String, Base64String } from '../types';

class SolanaService {
    constructor(private auth: Auth) {}

    private assertSolana() {
        if (this.auth.getChain()?.name?.toLowerCase() !== 'solana') {
            throw AuthError.unsupportedMethod();
        }
    }

    public signAndSendTransaction(transaction: Base58String): Promise<Base64String> {
        this.assertSolana();
        return this.auth.sendTransaction(transaction);
    }

    public async signTransaction(transaction: Base58String): Promise<Uint8Array> {
        this.assertSolana();
        const tx = await this.auth.sign('signTransaction', transaction);
        return Buffer.from(tx, 'base64');
    }

    public async signAllTransactions(transactions: Base58String[]): Promise<Uint8Array[]> {
        this.assertSolana();
        const txs = await this.auth.signAllTransactions(transactions);
        return txs.map((tx) => Buffer.from(tx, 'base64'));
    }

    public async signMessage(msg: Base58String): Promise<Uint8Array> {
        this.assertSolana();
        const signature = await this.auth.sign('signMessage', msg);
        return Buffer.from(signature, 'base64');
    }

    public async getAddress(): Promise<Base58String | undefined> {
        return this.auth.getSolanaAddress();
    }

    public getRpcUrl(): string {
        this.assertSolana();
        const domain = `${rpcUrl()}/solana`;
        return `${domain}?chainId=${this.auth.getChain().id}&projectUuid=${this.auth.config.projectId}&projectKey=${
            this.auth.config.clientKey
        }`;
    }
}

export default SolanaService;
