  const code = controller.languageCode;
  if (code.startsWith("en")) {
    return en;
  } else if (code.startsWith("ja")) {
    return ja;
  } else if (code.startsWith("ko")) {
    return ko;
  } else if (code === "zh_CN" || code === "zh-CN" || code === "zh") {
    return zh_CN;
  } else if (code === "zh_HK" || code === "zh-HK" || code === "zh_TW" || code === "zh-TW") {
    return zh_HK;
  } else {
    return en;
  }
}

