","keccak256":"0x52a8cfb0f5239d11b457dcdd1b326992ef672714ca8da71a157255bddd13f3ad","license":"MIT"},"contracts/MessageLibBase.sol":{"content":"// SPDX-License-Identifier: LZBL-1.2

pragma solidity ^0.8.19;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { Errors } from "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Errors.sol";

import { ILayerZeroExecutor } from "./interfaces/ILayerZeroExecutor.sol";
import { ILayerZeroTreasury } from "./interfaces/ILayerZeroTreasury.sol";

struct WorkerOptions {
    uint8 workerId;
    bytes options;
}

enum DeliveryState {
    Signing,
    Deliverable,
    Delivered,
    Waiting
}

abstract contract MessageLibBase is Ownable {
    address internal immutable endpoint;
    uint32 internal immutable localEid;
    uint256 internal immutable treasuryGasCap;

    // config
    address public treasury;

    // accumulated fees for workers and treasury
    mapping(address worker => uint256) public fees;

    event ExecutorFeePaid(address executor, uint256 fee);
    event TreasurySet(address treasury);

    // only the endpoint can call SEND() and setConfig()
    modifier onlyEndpoint() {
        require(endpoint == msg.sender, Errors.PERMISSION_DENIED);
        _;
    }

    constructor(address _endpoint, uint32 _localEid, uint256 _treasuryGasCap) {
        endpoint = _endpoint;
        localEid = _localEid;
        treasuryGasCap = _treasuryGasCap;
    }

    // ======================= Internal =======================
    function _assertMessageSize(uint256 _actual, uint256 _max) internal pure {
        require(_actual <= _max, Errors.INVALID_SIZE);
    }

    function _sendToExecutor(
        address _executor,
        uint32 _dstEid,
        address _sender,
        uint256 _msgSize,
        bytes memory _executorOptions
    ) internal returns (uint256 executorFee) {
        executorFee = ILayerZeroExecutor(_executor).assignJob(_dstEid, _sender, _msgSize, _executorOptions);
        if (executorFee > 0) {
            fees[_executor] += executorFee;
        }
        emit ExecutorFeePaid(_executor, executorFee);
    }

    function _sendToTreasury(
        address _sender,
        uint32 _dstEid,
        uint256 _totalNativeFee,
        bool _payInLzToken
    ) internal returns (uint256 treasuryNativeFee, uint256 lzTokenFee) {
        // fee should be in lzTokenFee if payInLzToken, otherwise in native
        (treasuryNativeFee, lzTokenFee) = _quoteTreasuryFee(_sender, _dstEid, _totalNativeFee, _payInLzToken);
        // if payInLzToken, handle in messagelib / endpoint
        if (treasuryNativeFee > 0) {
            fees[treasury] += treasuryNativeFee;
        }
    }

    function _quote(
        address _sender,
        uint32 _dstEid,
        uint256 _msgSize,
        bool _payInLzToken,
        bytes calldata _options
    ) internal view returns (uint256, uint256) {
        require(_options.length > 0, Errors.INVALID_ARGUMENT);

        (bytes memory executorOptions, WorkerOptions[] memory otherWorkerOptions) = _getExecutorAndOtherOptions(
            _options
        );

        // quote other workers
        (uint256 nativeFee, address executor, uint256 maxMsgSize) = _quoteWorkers(_sender, _dstEid, otherWorkerOptions);

        // assert msg size
        _assertMessageSize(_msgSize, maxMsgSize);

        // quote executor
        nativeFee += ILayerZeroExecutor(executor).getFee(_dstEid, _sender, _msgSize, executorOptions);

        // quote treasury
        (uint256 treasuryNativeFee, uint256 lzTokenFee) = _quoteTreasuryFee(_sender, _dstEid, nativeFee, _payInLzToken);
        if (treasuryNativeFee > 0) {
            nativeFee += treasuryNativeFee;
        }

        return (nativeFee, lzTokenFee);
    }

    function _quoteTreasuryFee(
        address _sender,
        uint32 _eid,
        uint256 _totalFee,
        bool _payInLzToken
    ) internal view returns (uint256 nativeFee, uint256 lzTokenFee) {
        if (treasury != address(0x0)) {
            try ILayerZeroTreasury(treasury).getFee(_sender, _eid, _totalFee, _payInLzToken) returns (
                uint256 treasuryFee
            ) {
                // success
                if (_payInLzToken) {
                    lzTokenFee = treasuryFee;
                } else {
                    // pay in native, make sure that the treasury fee is not higher than the cap
                    uint256 gasFeeEstimate = tx.gasprice * treasuryGasCap;
                    // cap is the max of total fee and gasFeeEstimate. this is to prevent apps from forcing the cap to 0.
                    uint256 nativeFeeCap = _totalFee > gasFeeEstimate ? _totalFee : gasFeeEstimate;
                    // to prevent the treasury from returning an overly high value to break the path
                    nativeFee = treasuryFee > nativeFeeCap ? nativeFeeCap : treasuryFee;
                }
            } catch {
                // failure, something wrong with treasury contract, charge nothing and continue
            }
        }
    }

    function _transferNative(address _to, uint256 _amount) internal {
        (bool success, ) = _to.call{ value: _amount }("");
        require(success, Errors.INVALID_STATE);
    }

    // for msg.sender only
    function _assertAndDebitAmount(address _to, uint256 _amount) internal {
        uint256 fee = fees[msg.sender];
        require(_to != address(0x0) && _amount <= fee, Errors.INVALID_ARGUMENT);
        unchecked {
            fees[msg.sender] = fee - _amount;
        }
    }

    function _setTreasury(address _treasury) internal {
        treasury = _treasury;
        emit TreasurySet(_treasury);
    }

    // ======================= Virtual =======================
    // For implementation to override
    function _quoteWorkers(
        address _oapp,
        uint32 _eid,
        WorkerOptions[] memory _options
    ) internal view virtual returns (uint256 nativeFee, address executor, uint256 maxMsgSize);

    function _getExecutorAndOtherOptions(
        bytes calldata _options
    ) internal view virtual returns (bytes memory executorOptions, WorkerOptions[] memory otherWorkerOptions);
}
","keccak256":"0x81c39f0974f8e492db34b0123761e67ac11fbd81f0ffb0c63c96a0575dfcb9fe","license":"LZBL-1.2"},"contracts/OutboundConfig.sol":{"content":"// SPDX-License-Identifier: LZBL-1.2

pragma solidity ^0.8.19;

import { Errors } from "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Errors.sol";

struct OutboundConfigStruct {
    uint32 maxMessageSize;
    uint64 outboundConfirmations;
    address executor;
}

/// extract it because it might be reused by future non-ULN msglibs
contract OutboundConfig {
    // Derived MessageLibs should never use this as config type
    uint32 internal constant CONFIG_TYPE_MAX_MESSAGE_SIZE = 1;
    uint32 internal constant CONFIG_TYPE_OUTBOUND_CONFIRMATIONS = 2;
    uint32 internal constant CONFIG_TYPE_EXECUTOR = 3;

    mapping(uint32 dstEid => OutboundConfigStruct) public defaultOutboundConfig;
    mapping(address oapp => mapping(uint32 dstEid => OutboundConfigStruct)) public outboundConfig;

    event DefaultOutboundConfigSet(uint32 eid, uint32 maxMessageSize, uint64 outboundConfirmations, address executor);
    event OutboundConfigSet(address oapp, uint32 eid, uint32 configType, bytes config);
    event OutboundConfigSnapshot(address oapp, uint32[] eids);
    event OutboundConfigReset(address oapp, uint32[] eids);

    // ============================ View ===================================
    function getOutboundConfigByType(
        uint32 _eid,
        address _oapp,
        uint256 _configType
    ) public view returns (bytes memory, bool) {
        OutboundConfigStruct storage config = outboundConfig[_oapp][_eid];
        if (_configType == CONFIG_TYPE_MAX_MESSAGE_SIZE) {
            uint32 maxMessageSize = config.maxMessageSize;
            if (maxMessageSize == 0) {
                return (abi.encode(defaultOutboundConfig[_eid].maxMessageSize), true);
            }
            return (abi.encode(maxMessageSize), false);
        } else if (_configType == CONFIG_TYPE_OUTBOUND_CONFIRMATIONS) {
            uint64 outboundConfirmations = config.outboundConfirmations;
            if (outboundConfirmations == 0) {
                return (abi.encode(defaultOutboundConfig[_eid].outboundConfirmations), true);
            }
            return (abi.encode(outboundConfirmations), false);
        } else if (_configType == CONFIG_TYPE_EXECUTOR) {
            address executor = config.executor;
            if (executor == address(0x0)) {
                return (abi.encode(defaultOutboundConfig[_eid].executor), true);
            }
            return (abi.encode(executor), false);
        } else {
            revert(Errors.NOT_IMPLEMENTED);
        }
    }

    function getDefaultOutboundConfigByType(uint32 _eid, uint32 _configType) public view returns (bytes memory) {
        OutboundConfigStruct storage config = defaultOutboundConfig[_eid];
        if (_configType == CONFIG_TYPE_MAX_MESSAGE_SIZE) {
            return abi.encode(config.maxMessageSize);
        } else if (_configType == CONFIG_TYPE_OUTBOUND_CONFIRMATIONS) {
            return abi.encode(config.outboundConfirmations);
        } else if (_configType == CONFIG_TYPE_EXECUTOR) {
            return abi.encode(config.executor);
        } else {
            revert(Errors.NOT_IMPLEMENTED);
        }
    }

    // @dev assuming most oapps use default, we get default as memory and custom as storage to save gas
    function getOutboundConfig(address _oapp, uint32 _dstEid) public view returns (OutboundConfigStruct memory) {
        OutboundConfigStruct memory rtnConfig = defaultOutboundConfig[_dstEid];
        require(rtnConfig.maxMessageSize > 0, Errors.INVALID_EID); // available remote eid

        OutboundConfigStruct storage customConfig = outboundConfig[_oapp][_dstEid];

        uint32 maxMessageSize = customConfig.maxMessageSize;
        if (maxMessageSize != 0) {
            rtnConfig.maxMessageSize = maxMessageSize;
        }

        uint64 outboundConfirmations = customConfig.outboundConfirmations;
        if (outboundConfirmations != 0) {
            rtnConfig.outboundConfirmations = outboundConfirmations;
        }

        address executor = customConfig.executor;
        if (executor != address(0x0)) {
            rtnConfig.executor = executor;
        }
        return rtnConfig;
    }

    // ============================ Internal ===================================
    function _setDefaultOutboundConfig(uint32 _eid, OutboundConfigStruct calldata _config) internal {
        require(_config.outboundConfirmations > 0 && _config.executor != address(0x0), Errors.INVALID_ARGUMENT);
        require(_config.maxMessageSize > 0, Errors.INVALID_SIZE);

        OutboundConfigStruct storage config = defaultOutboundConfig[_eid];
        config.executor = _config.executor;
        config.maxMessageSize = _config.maxMessageSize;
        config.outboundConfirmations = _config.outboundConfirmations;

        emit DefaultOutboundConfigSet(_eid, _config.maxMessageSize, _config.outboundConfirmations, _config.executor);
    }

    function _setOutboundConfigByType(address _oapp, uint32 _eid, uint32 _configType, bytes calldata _config) internal {
        OutboundConfigStruct storage config = outboundConfig[_oapp][_eid];
        if (_configType == CONFIG_TYPE_MAX_MESSAGE_SIZE) {
            uint32 maxMessageSize = abi.decode(_config, (uint32));
            config.maxMessageSize = maxMessageSize;
        } else if (_configType == CONFIG_TYPE_OUTBOUND_CONFIRMATIONS) {
            uint64 outboundConfirmations = abi.decode(_config, (uint64));
            config.outboundConfirmations = outboundConfirmations;
        } else if (_configType == CONFIG_TYPE_EXECUTOR) {
            address executor = abi.decode(_config, (address));
            config.executor = executor;
        } else {
            revert(Errors.NOT_IMPLEMENTED);
        }
        emit OutboundConfigSet(_oapp, _eid, _configType, _config);
    }

    function _snapshotOutboundConfig(uint32[] calldata _eids, address _oapp) internal {
        for (uint256 i = 0; i < _eids.length; ) {
            uint32 eid = _eids[i];
            outboundConfig[_oapp][eid] = getOutboundConfig(_oapp, eid);
            unchecked {
                ++i;
            }
        }
        emit OutboundConfigSnapshot(_oapp, _eids);
    }

    function _resetOutboundConfig(uint32[] calldata _eids, address _oapp) internal {
        for (uint256 i = 0; i < _eids.length; ) {
            delete outboundConfig[_oapp][_eids[i]];
            unchecked {
                ++i;
            }
        }
        emit OutboundConfigReset(_oapp, _eids);
    }
}
","keccak256":"0x067e7bd9a4f95f38744f082a5a1559a5fd4380aa7d3a8c0f83adbcbe9c738822","license":"LZBL-1.2"},"contracts/interfaces/ILayerZeroExecutor.sol":{"content":"// SPDX-License-Identifier: LZBL-1.2

pragma solidity >=0.8.0;

interface ILayerZeroExecutor {
    // @notice query price and assign jobs at the same time
    // @param _dstEid - the destination endpoint identifier
    // @param _sender - the source sending contract address. executors may apply price discrimination to senders
    // @param _calldataSize - dynamic data size of message + caller params
    // @param _options - optional parameters for extra service plugins, e.g. sending dust tokens at the destination chain
    function assignJob(
        uint32 _dstEid,
        address _sender,
        uint256 _calldataSize,
        bytes calldata _options
    ) external payable returns (uint256 price);

    // @notice query the executor price for relaying the payload and its proof to the destination chain
    // @param _dstEid - the destination endpoint identifier
    // @param _sender - the source sending contract address. executors may apply price discrimination to senders
    // @param _calldataSize - dynamic data size of message + caller params
    // @param _options - optional parameters for extra service plugins, e.g. sending dust tokens at the destination chain
    function getFee(
        uint32 _dstEid,
        address _sender,
        uint256 _calldataSize,
        bytes calldata _options
    ) external view returns (uint256 price);
}
