}
","keccak256":"0x8d8f99f60337be29bec6fe0bd52fac5e0c8bade60b373e4d24d4bfbdd1a0f978","license":"LZBL-1.2"},"contracts/uln/UlnBase.sol":{"content":"// SPDX-License-Identifier: LZBL-1.2

pragma solidity ^0.8.19;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { OutboundConfigStruct } from "../OutboundConfig.sol";
import { IUlnConfig, UlnConfigStruct, SetDefaultConfigParam } from "./interfaces/IUlnConfig.sol";
import { ILayerZeroVerifier } from "./interfaces/ILayerZeroVerifier.sol";
import { VerifierOptions } from "./libs/VerifierOptions.sol";
import { UlnOptions } from "./libs/UlnOptions.sol";
import { WorkerOptions } from "../MessageLibBase.sol";
import { UlnConfig } from "./UlnConfig.sol";

/// @dev includes the utility functions for checking ULN states and logics
contract UlnBase is Ownable {
    mapping(bytes32 headerHash => mapping(bytes32 payloadHash => mapping(address verifier => uint64 confirmations)))
        public hashLookup;
    IUlnConfig public immutable ulnConfig;

    event PayloadSigned(address verifier, bytes header, uint256 confirmations, bytes32 proofHash);
    event VerifierFeePaid(address[] verifiers, address[] optionalVerifiers, uint256[] fees);

    constructor() {
        ulnConfig = IUlnConfig(new UlnConfig(address(this)));
    }

    // ============================ OnlyOwner ===================================
    function setDefaultConfig(SetDefaultConfigParam[] calldata _params) external onlyOwner {
        ulnConfig.setDefaultConfig(_params);
    }

    // ============================ View ===================================
    function verified(
        address _verifier,
        bytes32 _headerHash,
        bytes32 _payloadHash,
        uint64 _requiredConfirmation
    ) public view returns (bool) {
        uint64 verifiedConfirmations = hashLookup[_headerHash][_payloadHash][_verifier];
        // return true if the verifier has signed enough confirmations
        return verifiedConfirmations >= _requiredConfirmation;
    }

    /// @dev requires that the MUST-Verifiers have signed the payload and there is enough OPTIONAL-Verifiers signed
    function verifyConditionMet(
        UlnConfigStruct memory _config,
        bytes32 _headerHash,
        bytes32 _payloadHash
    ) public view returns (bool) {
        uint64 requiredConfirmations = _config.inboundConfirmations;
        // iterate the must-have verifiers
        unchecked {
            for (uint256 i = 0; i < _config.verifierCount; ++i) {
                if (!verified(_config.verifiers[i], _headerHash, _payloadHash, requiredConfirmations)) {
                    // return false if any of the must-have verifiers haven't signed
                    return false;
                }
            }

            if (_config.verifierCount > 0 && _config.optionalVerifierCount == 0) {
                // returns true if all must-have verifiers have signed and there are no optional verifiers
                return true;
            } else {
                // then the optional ones
                uint256 optionalCount = 0;
                for (uint256 i = 0; i < _config.optionalVerifierCount; ++i) {
                    if (verified(_config.optionalVerifiers[i], _headerHash, _payloadHash, requiredConfirmations)) {
                        // increment the optional count if the optional verifier has signed
                        ++optionalCount;
                        if (optionalCount >= _config.optionalVerifierThreshold) {
                            // early return if the optional threshold has hit
                            return true;
                        }
                    }
                }
            }
        }

        // return false by default as a catch-all
        return false;
    }

    // ============================ Internal ===================================
    function _getVerifierFees(
        UlnConfigStruct memory _config,
        uint32 _dstEid,
        uint64 _outboundConfirmations,
        address _sender,
        bytes[] memory _optionsArray,
        uint8[] memory _verifierIds
    ) internal view returns (uint256 totalFee) {
        uint8 verifiersLength = _config.verifierCount + _config.optionalVerifierCount;
        for (uint256 i = 0; i < verifiersLength; ) {
            address verifier;
            bytes memory options;
            unchecked {
                verifier = i < _config.verifierCount
                    ? _config.verifiers[i]
                    : _config.optionalVerifiers[i - _config.verifierCount];

                for (uint256 j = 0; j < _verifierIds.length; ++j) {
                    if (_verifierIds[j] == i) {
                        options = _optionsArray[j];
                        break;
                    }
                }
            }
            totalFee += ILayerZeroVerifier(verifier).getFee(_dstEid, _outboundConfirmations, _sender, options);

            unchecked {
                ++i;
            }
        }
    }

    function _assignJobToVerifiers(
        mapping(address => uint256) storage _fees,
        UlnConfigStruct memory _config,
        ILayerZeroVerifier.AssignJobParam memory _param,
        bytes memory verifierOptions
    ) internal returns (uint256 totalFee, uint256[] memory verifierFees) {
        (bytes[] memory optionsArray, uint8[] memory verifierIds) = VerifierOptions.groupVerifierOptionsByIdx(
            verifierOptions
        );

        uint8 verifiersLength = _config.verifierCount + _config.optionalVerifierCount;
        verifierFees = new uint256[](verifiersLength);
        for (uint256 i = 0; i < verifiersLength; ) {
            address verifier;
            bytes memory options;
            unchecked {
                verifier = i < _config.verifierCount
                    ? _config.verifiers[i]
                    : _config.optionalVerifiers[i - _config.verifierCount];

                for (uint256 j = 0; j < verifierIds.length; ++j) {
                    if (verifierIds[j] == i) {
                        options = optionsArray[j];
                        break;
                    }
                }
            }

            verifierFees[i] = ILayerZeroVerifier(verifier).assignJob(_param, options);
            if (verifierFees[i] > 0) {
                _fees[verifier] += verifierFees[i];
                totalFee += verifierFees[i];
            }

            unchecked {
                ++i;
            }
        }
    }

    function _getExecutorAndOtherOptionsInternal(
        bytes calldata _options
    ) internal pure returns (bytes memory, WorkerOptions[] memory) {
        (bytes memory executorOpts, bytes memory verifierOpts) = UlnOptions.decode(_options);

        if (verifierOpts.length == 0) {
            return (executorOpts, new WorkerOptions[](0));
        }

        WorkerOptions[] memory workerOpts = new WorkerOptions[](1);
        workerOpts[0] = WorkerOptions(VerifierOptions.WORKER_ID, verifierOpts);
        return (executorOpts, workerOpts);
    }

    function _ulnSend(
        mapping(address => uint256) storage _fees,
        WorkerOptions[] memory _options,
        bytes memory _packetHeader,
        bytes32 _payloadHash,
        address _sender,
        uint32 _dstEid
    ) internal returns (uint256 totalFee, address executor, uint256 maxMsgSize) {
        (UlnConfigStruct memory config, OutboundConfigStruct memory outboundConfig) = ulnConfig.getUlnAndOutboundConfig(
            _sender,
            _dstEid
        );
        executor = outboundConfig.executor;
        maxMsgSize = outboundConfig.maxMessageSize;

        // if options is not empty, it must be verifier options
        bytes memory verifierOptions = _options.length == 0 ? bytes("") : _options[0].options;
        uint256[] memory verifierFees;
        (totalFee, verifierFees) = _assignJobToVerifiers(
            _fees,
            config,
            ILayerZeroVerifier.AssignJobParam(
                _dstEid,
                _packetHeader,
                _payloadHash,
                outboundConfig.outboundConfirmations,
                _sender
            ),
            verifierOptions
        );

        emit VerifierFeePaid(config.verifiers, config.optionalVerifiers, verifierFees);
    }

    function _quoteUlnWorkers(
        address _sender,
        uint32 _dstEid,
        WorkerOptions[] memory _options
    ) internal view returns (uint256 totalFee, address executor, uint256 maxMsgSize) {
        (UlnConfigStruct memory config, OutboundConfigStruct memory outboundConfig) = ulnConfig.getUlnAndOutboundConfig(
            _sender,
            _dstEid
        );
        executor = outboundConfig.executor;
        maxMsgSize = outboundConfig.maxMessageSize;

        // if options is not empty, it must be verifier options
        bytes memory verifierOptions = _options.length == 0 ? bytes("") : _options[0].options;
        (bytes[] memory optionsArray, uint8[] memory verifierIndices) = VerifierOptions.groupVerifierOptionsByIdx(
            verifierOptions
        );

        totalFee = _getVerifierFees(
            config,
            _dstEid,
            outboundConfig.outboundConfirmations,
            _sender,
            optionsArray,
            verifierIndices
        );
    }

    function _verify(bytes calldata _packetHeader, bytes32 _payloadHash, uint64 _confirmations) internal {
        hashLookup[keccak256(_packetHeader)][_payloadHash][msg.sender] = _confirmations;
        emit PayloadSigned(msg.sender, _packetHeader, _confirmations, _payloadHash);
    }
}
","keccak256":"0x0047b992a61e54030b870b6ca4b09a1326a300a3e0967c13a8cdc5d8692ab35b","license":"LZBL-1.2"},"contracts/uln/UlnConfig.sol":{"content":"// SPDX-License-Identifier: LZBL-1.2

pragma solidity ^0.8.19;

import { SafeCast } from "@openzeppelin/contracts/utils/math/SafeCast.sol";

import { Errors } from "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Errors.sol";

import { IUlnConfig, UlnConfigStruct, SetDefaultConfigParam } from "./interfaces/IUlnConfig.sol";
import { OutboundConfig, OutboundConfigStruct } from "../OutboundConfig.sol";

contract UlnConfig is OutboundConfig, IUlnConfig {
    using SafeCast for uint256;

    // Application config, extending from OutboundConfig
    uint32 internal constant CONFIG_TYPE_INBOUND_CONFIRMATIONS = 4;
    uint32 internal constant CONFIG_TYPE_VERIFIERS = 5;
    uint32 internal constant CONFIG_TYPE_OPTIONAL_VERIFIERS = 6;

    address public immutable uln;

    mapping(address oapp => mapping(uint32 eid => UlnConfigStruct)) internal ulnConfig;
    mapping(uint32 eid => UlnConfigStruct) internal defaultUlnConfig;

    event DefaultUlnConfigSet(
        uint32 eid,
        uint64 inboundConfirmations,
        address[] verifiers,
        address[] optionalVerifiers,
        uint8 optionalVerifierThreshold
    );
    event UlnConfigSet(address oapp, uint32 eid, uint32 configType, bytes config);
    event UlnConfigSnapshot(address oapp, uint32[] eids);
    event UlnConfigReset(address oapp, uint32[] eids);

    constructor(address _uln) {
        uln = _uln;
    }

    modifier onlyUln() {
        require(msg.sender == uln, Errors.PERMISSION_DENIED);
        _;
    }

    // ============================ OnlyUln =====================================

    function setDefaultConfig(SetDefaultConfigParam[] calldata _params) external onlyUln {
        for (uint256 i = 0; i < _params.length; ++i) {
            SetDefaultConfigParam calldata param = _params[i];

            uint256 verifierCount = param.verifiers.length;
            uint256 optionalVerifierCount = param.optionalVerifiers.length;

            require(param.inboundConfirmations > 0, Errors.INVALID_ARGUMENT);
            require(verifierCount + optionalVerifierCount <= type(uint8).max, Errors.INVALID_SIZE);
            require(param.optionalVerifierThreshold <= optionalVerifierCount, Errors.INVALID_SIZE);
            require(verifierCount > 0 || param.optionalVerifierThreshold > 0, Errors.INVALID_VERIFIERS); // at least one verifier
            // threshold must be > 0 if optional verifiers are present
            if (optionalVerifierCount > 0) require(param.optionalVerifierThreshold > 0, Errors.INVALID_SIZE);

            // we can only assert the nonce duplication in each list respectively. the two lists might have duplicated ones
            // should do sanity check before submitting to the config here
            _assertNoDuplicates(param.verifiers, verifierCount);
            _assertNoDuplicates(param.optionalVerifiers, optionalVerifierCount);

            defaultUlnConfig[param.eid] = UlnConfigStruct(
                param.inboundConfirmations,
                false, // unused for default config
                false, // unused for default config
                uint8(verifierCount),
                uint8(optionalVerifierCount),
                param.optionalVerifierThreshold,
                param.verifiers,
                param.optionalVerifiers
            );
            emit DefaultUlnConfigSet(
                param.eid,
                param.inboundConfirmations,
                param.verifiers,
                param.optionalVerifiers,
                param.optionalVerifierThreshold
            );

            _setDefaultOutboundConfig(param.eid, param.outboundConfig);
        }
    }

    function setConfigByType(
        uint32 _remoteEid,
        address _oapp,
        uint32 _configType,
        bytes calldata _config
    ) external onlyUln {
        require(isSupportedEid(_remoteEid), Errors.INVALID_EID);

        if (_configType <= CONFIG_TYPE_EXECUTOR) {
            _setOutboundConfigByType(_oapp, _remoteEid, _configType, _config);
            return;
        }

        UlnConfigStruct storage config = ulnConfig[_oapp][_remoteEid];
        if (_configType == CONFIG_TYPE_INBOUND_CONFIRMATIONS) {
            uint64 blockConfirmations = abi.decode(_config, (uint64));
            config.inboundConfirmations = blockConfirmations;
        } else if (_configType == CONFIG_TYPE_VERIFIERS) {
            (bool useCustomVerifiers, address[] memory verifiers) = abi.decode(_config, (bool, address[]));

            // if useCustomVerifiers is false, verifiers list must be empty
            // otherwise, verifiers list must be sorted by ascending order and contain no duplicates
            uint8 verifierCount = verifiers.length.toUint8();
            if (useCustomVerifiers) {
                _assertNoDuplicates(verifiers, verifierCount);
            } else {
                require(verifierCount == 0, Errors.INVALID_SIZE);
            }

            config.useCustomVerifiers = useCustomVerifiers;
            config.verifierCount = verifierCount;
            config.verifiers = verifiers;

            // assert verifier list by calling getUlnConfig
            getUlnConfig(_oapp, _remoteEid);
        } else if (_configType == CONFIG_TYPE_OPTIONAL_VERIFIERS) {
            // should set the optional verifiers and the threshold at the same time and dont support the case that
            // set the custom threshold but with the default optional verifiers. If so, when changing the default optional
            // verifiers, the threshold may be more than the optional verifier count
            (bool useCustomOptionalVerifiers, address[] memory optionalVerifiers, uint8 threshold) = abi.decode(
                _config,
                (bool, address[], uint8)
            );

            // 1. if useCustomOptionalVerifiers is false, all other fields must be empty
            // 2. if useCustomOptionalVerifiers is true but optionalVerifiers is empty, threshold must be 0
            // 3. if useCustomOptionalVerifiers is true and optionalVerifiers is not empty, threshold must be > 0 and
            // verifiers list must be sorted by ascending order and contain no duplicates
            uint8 optionalVerifierCount = optionalVerifiers.length.toUint8();
            if (useCustomOptionalVerifiers && optionalVerifierCount > 0) {
                require(threshold > 0 && threshold <= optionalVerifierCount, Errors.INVALID_SIZE);
                _assertNoDuplicates(optionalVerifiers, optionalVerifierCount);
            } else {
                require(optionalVerifierCount == 0 && threshold == 0, Errors.INVALID_SIZE);
            }

            config.useCustomOptionalVerifiers = useCustomOptionalVerifiers;
            config.optionalVerifierCount = optionalVerifierCount;
            config.optionalVerifiers = optionalVerifiers;
            config.optionalVerifierThreshold = threshold;

            // assert verifier list by calling getUlnConfig
            getUlnConfig(_oapp, _remoteEid);
        } else {
            revert(Errors.NOT_IMPLEMENTED);
        }
        emit UlnConfigSet(_oapp, _remoteEid, _configType, _config);
    }

    function snapshotConfig(uint32[] calldata _eids, address _oapp) external onlyUln {
        for (uint256 i = 0; i < _eids.length; ) {
            uint32 eid = _eids[i];
            UlnConfigStruct memory config = getUlnConfig(_oapp, eid); // already assert if not supported eid
            config.useCustomVerifiers = true;
            config.useCustomOptionalVerifiers = true;
            ulnConfig[_oapp][eid] = config;
            unchecked {
                ++i;
            }
        }
        emit UlnConfigSnapshot(_oapp, _eids);

        _snapshotOutboundConfig(_eids, _oapp);
    }

    function resetConfig(uint32[] calldata _eids, address _oapp) external onlyUln {
        for (uint256 i = 0; i < _eids.length; ) {
            uint32 eid = _eids[i];
            require(isSupportedEid(eid), Errors.INVALID_EID);
            delete ulnConfig[_oapp][eid];
            unchecked {
                ++i;
            }
        }
        emit UlnConfigReset(_oapp, _eids);

        _resetOutboundConfig(_eids, _oapp);
    }

    // ============================ View =====================================
    // @dev assuming most oapps use default, we get default as memory and custom as storage to save gas
    function getUlnConfig(address _oapp, uint32 _remoteEid) public view returns (UlnConfigStruct memory) {
        UlnConfigStruct memory rtnConfig = defaultUlnConfig[_remoteEid];

        // assert supported eid
        require(rtnConfig.inboundConfirmations > 0, Errors.INVALID_EID);

        UlnConfigStruct storage customConfig = ulnConfig[_oapp][_remoteEid];

        uint64 inboundConfirmations = customConfig.inboundConfirmations;
        if (inboundConfirmations != 0) {
            rtnConfig.inboundConfirmations = inboundConfirmations;
        }

        if (customConfig.useCustomVerifiers) {
            rtnConfig.useCustomVerifiers = true;
            rtnConfig.verifiers = customConfig.verifiers;
            rtnConfig.verifierCount = customConfig.verifierCount;
        }

        if (customConfig.useCustomOptionalVerifiers) {
            rtnConfig.useCustomOptionalVerifiers = true;
            rtnConfig.optionalVerifiers = customConfig.optionalVerifiers;
            rtnConfig.optionalVerifierCount = customConfig.optionalVerifierCount;
            rtnConfig.optionalVerifierThreshold = customConfig.optionalVerifierThreshold;
        }

        // assert verifier list
        // it is possible for sender to configure nil verifiers
        require(rtnConfig.verifierCount > 0 || rtnConfig.optionalVerifierThreshold > 0, Errors.VERIFIERS_UNAVAILABLE);
        // verifier options restricts total verifiers to 255
        require(rtnConfig.verifierCount + rtnConfig.optionalVerifierCount <= type(uint8).max, Errors.INVALID_SIZE);

        return rtnConfig;
    }

    function getUlnAndOutboundConfig(
        address _oapp,
        uint32 _remoteEid
    ) external view returns (UlnConfigStruct memory, OutboundConfigStruct memory) {
        return (getUlnConfig(_oapp, _remoteEid), getOutboundConfig(_oapp, _remoteEid));
    }

    function getConfigByType(
        uint32 _remoteEid,
        address _oapp,
        uint32 _configType
    ) external view returns (bytes memory, bool) {
        UlnConfigStruct storage config = ulnConfig[_oapp][_remoteEid];

        // type 1/2/3 are for the outboundConfig
        if (_configType <= CONFIG_TYPE_EXECUTOR) {
            return getOutboundConfigByType(_remoteEid, _oapp, _configType);
        } else if (_configType == CONFIG_TYPE_INBOUND_CONFIRMATIONS) {
            uint64 inboundConfirmations = config.inboundConfirmations;
            if (inboundConfirmations == 0) {
                return (abi.encode(defaultUlnConfig[_remoteEid].inboundConfirmations), true);
            }
            return (abi.encode(inboundConfirmations), false);
        } else if (_configType == CONFIG_TYPE_VERIFIERS) {
            if (config.useCustomVerifiers) {
                return (abi.encode(config.verifiers), false);
            }
            return (abi.encode(defaultUlnConfig[_remoteEid].verifiers), true);
        } else if (_configType == CONFIG_TYPE_OPTIONAL_VERIFIERS) {
            if (config.useCustomOptionalVerifiers) {
                return (abi.encode(config.optionalVerifiers, config.optionalVerifierThreshold), false);
            }
            UlnConfigStruct storage defaultConfig = defaultUlnConfig[_remoteEid];
            return (abi.encode(defaultConfig.optionalVerifiers, defaultConfig.optionalVerifierThreshold), true);
        }
        revert(Errors.NOT_IMPLEMENTED);
    }

    function getDefaultConfigByType(uint32 _remoteEid, uint32 _configType) external view returns (bytes memory) {
        if (_configType <= CONFIG_TYPE_EXECUTOR) {
            return getDefaultOutboundConfigByType(_remoteEid, _configType);
        } else if (_configType == CONFIG_TYPE_INBOUND_CONFIRMATIONS) {
            UlnConfigStruct storage config = defaultUlnConfig[_remoteEid];
            return abi.encode(config.inboundConfirmations);
        } else if (_configType == CONFIG_TYPE_VERIFIERS) {
            UlnConfigStruct storage config = defaultUlnConfig[_remoteEid];
            return abi.encode(config.verifiers);
        } else if (_configType == CONFIG_TYPE_OPTIONAL_VERIFIERS) {
            UlnConfigStruct storage config = defaultUlnConfig[_remoteEid];
            return abi.encode(config.optionalVerifiers, config.optionalVerifierThreshold);
        }
        revert(Errors.NOT_IMPLEMENTED);
    }

    function isSupportedEid(uint32 _remoteEid) public view returns (bool) {
        return defaultUlnConfig[_remoteEid].inboundConfirmations > 0;
    }

    /// @dev Get the uln config without the default config for the given remoteEid.
    function getRawUlnConfig(address _oapp, uint32 _remoteEid) external view returns (UlnConfigStruct memory) {
        return ulnConfig[_oapp][_remoteEid];
    }

    function getDefaultUlnConfig(uint32 _remoteEid) external view returns (UlnConfigStruct memory) {
        return defaultUlnConfig[_remoteEid];
    }

    // ============================ Internal =====================================

    function _assertNoDuplicates(address[] memory _verifiers, uint256 _verifierCount) internal pure {
        address lastVerifier = address(0);
        for (uint256 i = 0; i < _verifierCount; i++) {
            address verifier = _verifiers[i];
            require(verifier > lastVerifier, Errors.UNSORTED); // to ensure no duplicates
            lastVerifier = verifier;
        }
    }
}
