pragma solidity ^0.8.19;

import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC165 } from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import { ERC165 } from "@openzeppelin/contracts/utils/introspection/ERC165.sol";

import { Packet, PacketForQuote, Origin } from "@layerzerolabs/lz-evm-protocol-v2/contracts/MessagingStructs.sol";
import { ILayerZeroEndpointV2, MessagingFee } from "@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/ILayerZeroEndpointV2.sol";
import { IMessageLib } from "@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/IMessageLib.sol";
import { Errors } from "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Errors.sol";
import { PacketV1Codec } from "@layerzerolabs/lz-evm-protocol-v2/contracts/messagelib/libs/PacketV1Codec.sol";

import { MessageLibBase, DeliveryState, WorkerOptions } from "./MessageLibBase.sol";

/// the base contract for msglib on Endpoint V2
abstract contract MessageLibBaseE2 is MessageLibBase, ERC165, IMessageLib {
    using SafeERC20 for IERC20;
    using PacketV1Codec for bytes;

    address internal immutable altFeeToken;

    event NativeOrAltTokenFeeWithdrawn(address worker, address receiver, uint256 amount);
    event LzTokenFeeWithdrawn(address lzToken, address receiver, uint256 amount);

    constructor(
        address _endpoint,
        uint256 _treasuryGasCap
    ) MessageLibBase(_endpoint, ILayerZeroEndpointV2(_endpoint).eid(), _treasuryGasCap) {
        altFeeToken = ILayerZeroEndpointV2(_endpoint).altFeeToken();
    }

    function supportsInterface(bytes4 _interfaceId) public view virtual override(ERC165, IERC165) returns (bool) {
        return _interfaceId == type(IMessageLib).interfaceId || super.supportsInterface(_interfaceId);
    }

    // ========================= OnlyEndpoint =========================
    // @dev this function is marked as virtual and public for testing purpose
    function send(
        Packet calldata _packet,
        bytes calldata _options,
        bool _payInLzToken
    ) public virtual onlyEndpoint returns (MessagingFee memory, bytes memory) {
        (bytes memory encodedPacket, uint256 totalNativeFee) = _sendToWorkers(_packet, _options);

        (uint256 treasuryNativeFee, uint256 lzTokenFee) = _sendToTreasury(
            _packet.sender,
            _packet.dstEid,
            totalNativeFee,
            _payInLzToken
        );
        totalNativeFee += treasuryNativeFee;

        return (MessagingFee(totalNativeFee, lzTokenFee), encodedPacket);
    }

    // ========================= OnlyOnwer =========================
    function setTreasury(address _treasury) external onlyOwner {
        _setTreasury(_treasury);
    }

    // ========================= External =========================
    /// @dev E2 only
    function withdrawFee(address _to, uint256 _amount) external {
        _assertAndDebitAmount(_to, _amount);

        if (altFeeToken != address(0x0)) {
            IERC20(altFeeToken).safeTransfer(_to, _amount);
        } else {
            _transferNative(_to, _amount);
        }
        emit NativeOrAltTokenFeeWithdrawn(msg.sender, _to, _amount);
    }

    /// @dev _lzToken is a user-supplied value because lzToken might change in the endpoint before all lzToken can be taken out
    /// @dev E2 only
    /// @dev treasury only function
    function withdrawLzTokenFee(address _lzToken, address _to, uint256 _amount) external {
        require(msg.sender == treasury, Errors.PERMISSION_DENIED);

        // lz token cannot be alt token
        require(
            ILayerZeroEndpointV2(endpoint).altFeeToken() != _lzToken && _to != address(0x0),
            Errors.INVALID_ARGUMENT
        );

        IERC20(_lzToken).safeTransfer(_to, _amount);
        emit LzTokenFeeWithdrawn(_lzToken, _to, _amount);
    }

    // ========================= View =========================
    function quote(
        PacketForQuote calldata _packet,
        bool _payInLzToken,
        bytes calldata _options
    ) external view returns (MessagingFee memory) {
        (uint256 nativeFee, uint256 lzTokenFee) = _quote(
            _packet.sender,
            _packet.dstEid,
            _packet.message.length,
            _payInLzToken,
            _options
        );
        return MessagingFee(nativeFee, lzTokenFee);
    }

    // ========================= Internal =========================
    function _sendToWorkers(
        Packet calldata _packet,
        bytes calldata _options
    ) internal returns (bytes memory encodedPacket, uint256 totalNativeFee) {
        (bytes memory executorOptions, WorkerOptions[] memory otherWorkerOptions) = _getExecutorAndOtherOptions(
            _options
        );

        (uint256 otherWorkerFees, address executor, uint256 maxMsgSize, bytes memory packetBytes) = _send(
            _packet,
            otherWorkerOptions
        );
        totalNativeFee += otherWorkerFees;

        uint256 msgSize = _packet.message.length;
        _assertMessageSize(msgSize, maxMsgSize);

        totalNativeFee += _sendToExecutor(executor, _packet.dstEid, _packet.sender, msgSize, executorOptions);
        encodedPacket = packetBytes;
    }

    function _endpointDeliverable(
        uint32 _srcEid,
        address _receiver,
        bytes calldata _packetHeader
    ) internal view returns (DeliveryState) {
        // 1. check endpoint deliverable
        // not checking in the internal function as it would be checked by the endpoint
        Origin memory origin = Origin(_srcEid, _packetHeader.sender(), _packetHeader.nonce());

        // if endpoint did not revert on deliverable check, it will return true if the message is deliverable. false if the message has been delivered
        bool endpointDeliverable = ILayerZeroEndpointV2(endpoint).deliverable(origin, address(this), _receiver);
        if (!endpointDeliverable) return DeliveryState.Delivered;

        // 2. check if has payload
        // endpoint allows redelivery, check if it has already been delivered
        bool hasPayload = ILayerZeroEndpointV2(endpoint).hasPayloadHash(
            _receiver,
            origin.srcEid,
            origin.sender,
            origin.nonce
        );
        if (hasPayload) return DeliveryState.Delivered;

        return DeliveryState.Deliverable;
    }

    // ======================= Virtual =======================
    // For implementation to override
    function _send(
        Packet calldata _packet,
        WorkerOptions[] memory _options
    )
        internal
        virtual
        returns (uint256 otherWorkerFees, address executor, uint256 maxMsgSize, bytes memory encodedPacket);

    // receive native token from endpoint
    receive() external payable virtual {}
}
